func findLadders(beginWord string, endWord string, wordList []string) [][]string {
	res := make([][]string, 0)
	dict := StringList2BoolMap(wordList)
	paths := [][]string{{beginWord}}
	end := false

	//遍历toVisit队列
	for len(paths) > 0 && !end {
		l := len(paths)

		//每次遍历一层
		visited := make(map[string]bool)
		for i := 0; i < l; i++ {
			p := paths[i]

			//本步使用的单词
			curWord := p[len(p)-1]
			curRune := []rune(curWord)

			//遍历该单词的每一位
			for j := range curRune {
				c := curRune[j]
				//修改每一位字母
				for k := byte(0); k < 26; k++ {
					curRune[j] = rune('a' + k)
					newWord := string(curRune)
					if newWord == curWord || !dict[newWord] {
						continue
					}
					visited[newWord] = true

					if newWord == endWord {
						end = true
						path := appendWordToPath(paths[i], endWord)
						res = append(res, path)
						//如果旧单词距离 + 1 > 新单词，说明之前有更短的路径到新单词，则本次不用旧单词 + 1
					} else {
						path := appendWordToPath(paths[i], newWord)
						paths = append(paths, path)
					}
				}
				curRune[j] = c
			}
		}

		//弹出本层单词
		paths = paths[l:]

		//将本层单词从字典中移除，避免后续走重复路径
		for k := range visited {
			dict[k] = false
		}
	}

	return res
}

func StringList2BoolMap(list []string) map[string]bool {
	var m = make(map[string]bool)
	for _, value := range list {
		m[value] = true
	}
	return m
}

func appendWordToPath(paths []string, word string) []string {
	p := make([]string, len(paths)+1)
	copy(p, paths)
	p[len(paths)] = word
	return p
}
